export const deckStore=defineStore('deck',{
  state:()=>({

    settings:{
      iconSize:'middle'// small large
    },
    editing:false,//可编辑
    deletedItems:[] as string[],
    /**
     * 临时性布局，一般是指拖动过程中的布局
     */
    layouts: {

    },
    /**
     * 存储后的布局，一般是指确认之后的布局
     */
    defaultLayouts:{

    },
    grids:[

    ] as IGrid[]
  }),
  actions:{
    initGrids(){
      console.log(initDeck)
      this.grids=JSON.parse(initDeck)
    },
    /**
     * 添加/编辑一个按钮
     * @param button
     * @param currentGrid
     */
    addButton(button,currentGrid){
      let found=currentGrid.children.find(item=>{
        return item.id===button.id
      })
      if(found){
        Object.assign(found,button)
        return 'edit'
      }else{
        currentGrid.children.push(button)
        return 'add'
      }
      // if (!this.currentItem) {
      //   this.newItem = button
      //   this.$refs['grid' + this.currentGridId][0].update()
      //   this.currentGrid.children.push(this.newItem)
      //
      // } else {
      //   Object.keys(this.currentItem).forEach(key => {
      //     this.currentItem[key] = button[key]
      //   })
      //
      // }
    }
  },
  persist: {
    enabled: true,
    strategies: [{
      // 自定义存储的 key，默认是 store.$id
      // 可以指定任何 extends Storage 的实例，默认是 sessionStorage
      storage: dbStorage,
      // state 中的字段名，按组打包储存
    }]
  }
})
